        fft_value_valid <= '0';
                
        wait until fft_output_valid = '1' and rising_edge(clk);
                
        for i in 0 to C_FFT_LENGTH - 1 loop
            assert fft_output = OUTPUT(i);
            wait until rising_edge(clk);
        end loop;
        
        wait until fft_output_valid = '0';
                
        -- fill pipeline with extra data
        for i in 0 to C_FFT_LENGTH - 1 loop
            fft_value_valid <= '1';
            fft_value <= INPUT(i);
        	wait until rising_edge(clk);
        end loop;
        
        fft_value_valid <= '0';
                
        wait until fft_output_valid = '1' and rising_edge(clk);
        
		for i in 0 to C_FFT_LENGTH - 1 loop
			assert fft_output = OUTPUT(i);
            wait until rising_edge(clk);
		end loop;
        
        stop <= '1';
        
        wait;
    end process;

end;